module.exports = function(RED) {
  function BPTokenAddAttributeNode(n) {
      RED.nodes.createNode(this, n);
      this.name = n.name;
      this.attribute = n.attribute;
      this.value = n.value;
      this.on('input', function(msg) {
      });
  }
  RED.nodes.registerType("add-attribute",BPTokenAddAttributeNode);
}
