module.exports = function(RED) {
  function BPTokenChangeConditionNode(n) {
      RED.nodes.createNode(this, n);
      this.name = n.name;
      this.condition = n.condition;
      this.on('input', function(msg) {
      });
  }
  RED.nodes.registerType("change-condition",BPTokenChangeConditionNode);
}
